                                <span style="margin-left:45px">æ—¶é•¿ï¼š{{fileDuration}}</span>
                            </div>
                        </div>
                    </div>
                    <div
                        v-if="this.uploadType==='File'"
                        class="uploader-file-size"
                    >å¤§å°ï¼š{{formatedSize}}</div>
                    <div class="uploader-file-meta"></div>
                    <div class="uploader-file-actions" v-show="status !== 'success'">
                        <!-- <span class="uploader-file-pause" @click="pause"></span> æš‚åœ-->
                        <!-- é‡æ–°å¼€å§‹ -->
                        <!-- <span class="uploader-file-resume" @click="resume">ï¸</span>  -->
                        <!-- é‡è¯• -->
                        <span class="uploader-file-retry" v-if="status === 'error'" @click="retry"></span>
