</template>

<script>
import Uploader from "simple-uploader.js";
import events from "../common/file-events";
import { secondsToStr } from "../common/utils";

const COMPONENT_NAME = "uploader-file";

export default {
    name: COMPONENT_NAME,
    props: {
        file: {
            type: Object,
            default() {
                return {};
            }
        },
        list: {
            type: Boolean,
            default: false
        },
        uploadType: {
            type: String
        }
    },
    data() {
        return {
            response: null,
            paused: false,
            error: false,
            averageSpeed: 0,
            currentSpeed: 0,
            isComplete: false,
            isUploading: false,
            size: 0,
            formatedSize: "",
            uploadedSize: 0,
            progress: 0,
            timeRemaining: 0,
            type: "",
            extension: "",
            progressingClass: "",
            fileDuration: "" // zxb
        };
    },
    computed: {
        fileCategory() {
            // console.log(this.file, "00090000");

            let url = URL.createObjectURL(this.file.file);
           
            var audioElement = new Audio(url);
            console.log(audioElement);
            var duration;
            audioElement.addEventListener("loadedmetadata", _event => {
                duration = audioElement.duration;
                console.log(duration + "s");
                this.fileDuration = secondsToStr(duration);
            });
          
            const extension = this.extension;
            const isFolder = this.file.isFolder;
            let type = isFolder ? "folder" : "unknown";
            const categoryMap = this.file.uploader.opts.categoryMap;
            console.log(categoryMap);
            const typeMap = categoryMap || {
                image: ["gif", "jpg", "jpeg", "png", "bmp", "webp"],
                video: [
                    "mp4",
                    "m3u8",
                    "rmvb",
                    "avi",
                    "swf",
                    "3gp",
                    "mkv",
                    "flv"
                ],
                audio: ["mp3", "wav", "wma", "ogg", "aac", "flac"],
                document: [
                    "doc",
                    "txt",
                    "docx",
                    "pages",
                    "epub",
                    "pdf",
                    "numbers",
                    "csv",
                    "xls",
                    "xlsx",
                    "keynote",
                    "ppt",
                    "pptx"
                ],
                zip: [
                    "rar",
                    "zip",
                    "cab",
                    "iso",
                    "jar",
                    "ace",
                    "7z",
                    "tar",
                    "gz",
                    "arj",
                    "lzh",
                    "uue",
                    "bz2",
                    "z"
                ]
            };
            Object.keys(typeMap).forEach(_type => {
                const extensions = typeMap[_type];
                if (extensions.indexOf(extension) > -1) {
                    type = _type;
                }
            });
            return type;
        },
        progressStyle() {
            const progress = Math.floor(this.progress * 100);
            const style = `translateX(${Math.floor(progress - 90)}%)`;
            return {
                progress: `${progress}%`,
                webkitTransform: style,
                mozTransform: style,
                msTransform: style,
                transform: style
            };
        },
        formatedAverageSpeed() {
            return `${Uploader.utils.formatSize(this.averageSpeed)} / s`;
        },
        status() {
            const isUploading = this.isUploading;
            const isComplete = this.isComplete;
            const isError = this.error;
            const paused = this.paused;
            if (isComplete) {
                return "success";
            } else if (isError) {
                return "error";
            } else if (isUploading) {
                return "uploading";
            } else if (paused) {
                return "paused";
            } else {
                return "waiting";
            }
        },
        statusText() {
            const status = this.status;
            const fileStatusText = this.file.uploader.fileStatusText;
            let txt = status;
            if (typeof fileStatusText === "function") {
                txt = fileStatusText(status, this.response);
            } else {
                txt = fileStatusText[status];
            }
            return txt || status;
        },
        formatedFileDuration(timeRemaining) {
            this.fileDuration = secondsToStr(timeRemaining);
        },
        formatedTimeRemaining() {
            const timeRemaining = this.timeRemaining;
            const file = this.file;
            if (
                timeRemaining === Number.POSITIVE_INFINITY ||
                timeRemaining === 0
            ) {
                return "";
            }
            let parsedTimeRemaining = secondsToStr(timeRemaining);
            const parseTimeRemaining = file.uploader.opts.parseTimeRemaining;
            if (parseTimeRemaining) {
                parsedTimeRemaining = parseTimeRemaining(
                    timeRemaining,
                    parsedTimeRemaining
                );
            }
            return parsedTimeRemaining;
        }
    },
    watch: {
        status(newStatus, oldStatus) {
            if (
                oldStatus &&
                newStatus === "uploading" &&
                oldStatus !== "uploading"
            ) {
                this.tid = setTimeout(() => {
                    this.progressingClass = "uploader-file-progressing";
                }, 200);
            } else {
                clearTimeout(this.tid);
                this.progressingClass = "";
            }
        }
    },
    methods: {
        _actionCheck() {
            this.paused = this.file.paused;
            this.error = this.file.error;
            this.isUploading = this.file.isUploading();
        },
        pause() {
            this.file.pause();
            this._actionCheck();
            this._fileProgress();
        },
        resume() {
            this.file.resume();
            this._actionCheck();
        },
        remove() {
            this.file.cancel();
        },
        retry() {
            this.file.retry();
            this._actionCheck();
        },
        processResponse(message) {
            let res = message;
            try {
                res = JSON.parse(message);
            } catch (e) {}
            this.response = res;
        },
        fileEventsHandler(event, args) {
            const rootFile = args[0];
            const file = args[1];
            const target = this.list ? rootFile : file;
            if (this.file === target) {
                if (this.list && event === "fileSuccess") {
                    this.processResponse(args[2]);
                    return;
                }
                this[`_${event}`].apply(this, args);
            }
        },
        _fileProgress() {
            this.progress = this.file.progress();
            this.averageSpeed = this.file.averageSpeed;
            this.currentSpeed = this.file.currentSpeed;
            this.timeRemaining = this.file.timeRemaining();
            this.uploadedSize = this.file.sizeUploaded();
            this._actionCheck();
        },
        _fileSuccess(rootFile, file, message) {
            if (rootFile) {
                this.processResponse(message);
            }
        
            this._fileProgress();
            this.error = false;
            this.isComplete = true;
            this.isUploading = false;
        },
        _fileComplete() {
            this._fileSuccess();
        },
        _fileError(rootFile, file, message) {
            this._fileProgress();
            this.processResponse(message);
            this.error = true;
            this.isComplete = false;
            this.isUploading = false;
        },
        coverImg(fileCategory) {
            if (this.uploadType === "File") {
                if (this.fileCategory === "document") {
                    return "document";
                } else if (this.fileCategory === "video") {
                    return "video";
                } else if (this.fileCategory === "image") {
                    return "image";
                } else if (this.fileCategory === "audio") {
                    return "audio";
                } else if (this.fileCategory === "audio") {
                    return "zip";
                } else {
                    return "other";
                }
            } else if (this.uploadType === "Video") {
                return "video-cover";
            }else if(this.uploadType === "Audio"){
                 return "audio";
            }
        },
        statusTextImg(status) {
            if (status === "success") {
                return "zxb-success";
            } else if (status === "error") {
                return "zxb-error";
            }
        }
    },
    mounted() {
        const staticProps = ["paused", "error", "averageSpeed", "currentSpeed"];
        const fnProps = [
            "isComplete",
            "isUploading",
            {
                key: "size",
                fn: "getSize"
            },
            {
                key: "formatedSize",
                fn: "getFormatSize"
            },
            {
                key: "uploadedSize",
                fn: "sizeUploaded"
            },
            "progress",
            "timeRemaining",
            {
                key: "type",
                fn: "getType"
            },
            {
                key: "extension",
                fn: "getExtension"
            }
        ];
        staticProps.forEach(prop => {
            this[prop] = this.file[prop];
        });
        fnProps.forEach(fnProp => {
            if (typeof fnProp === "string") {
                this[fnProp] = this.file[fnProp]();
            } else {
                this[fnProp.key] = this.file[fnProp.fn]();
            }
        });

        const handlers = (this._handlers = {});
        const eventHandler = event => {
            handlers[event] = (...args) => {
                this.fileEventsHandler(event, args);
            };
            return handlers[event];
        };
        events.forEach(event => {
            this.file.uploader.on(event, eventHandler(event));
        });
    },
    destroyed() {
        events.forEach(event => {
            this.file.uploader.off(event, this._handlers[event]);
        });
        this._handlers = null;
    }
};
</script>

